    @Test
    public void testReverseWordsInString4() {
        ASSERT.that(
                sut.findSubstring("abababab", new String[] { "a", "b", "a" }))
                .iteratesAs(Arrays.asList(0, 2, 4));
    }

    @Test
    public void testReverseWordsInString3() {
        ASSERT.that(
                sut.findSubstring(
                        "lingmindraboofooowingdingbarrwingmonkeypoundcake",
                        new String[] { "fooo", "barr", "wing", "ding", "wing" }))
                .iteratesAs(Arrays.asList(13));
    }

    @Test
    public void testReverseWordsInString2() {
        ASSERT.that(sut.findSubstring("aaa", new String[] { "a", "a" }))
                .iteratesAs(Arrays.asList(0, 1));
    }

    @Test
    public void testReverseWordsInString1() {
        ASSERT.that(
                sut.findSubstring("barfoothefoobarman", new String[] { "foo",
                        "bar" })).iteratesAs(Arrays.asList(0, 9));
    }
